(414, 1, '2019-09-07 14:23:17', '2019-09-07 14:23:17', '<!DOCTYPE html>\r\n<html>\r\n<title>W3.CSS</title>\r\n<meta name=\"viewport\" content=\"width=device-width, initial-scale=1\">\r\n<link rel=\"stylesheet\" href=\"https://www.w3schools.com/w3css/4/w3.css\">\r\n<body>\r\n\r\n<div class=\"w3-sidebar w3-bar-block w3-light-grey w3-card\" style=\"width:130px\">\r\n  <h5 class=\"w3-bar-item\">Menu</h5>\r\n  <button class=\"w3-bar-item w3-button tablink\" onclick=\"openCity(event, \'London\')\">London</button>\r\n  <button class=\"w3-bar-item w3-button tablink\" onclick=\"openCity(event, \'Paris\')\">Paris</button>\r\n  <button class=\"w3-bar-item w3-button tablink\" onclick=\"openCity(event, \'Tokyo\')\">Tokyo</button>\r\n</div>\r\n\r\n<div style=\"margin-left:130px\">\r\n  <div class=\"w3-padding\">Vertical Tab Example (sidebar)</div>\r\n\r\n  <div id=\"London\" class=\"w3-container city\" style=\"display:none\">\r\n    <h2>London</h2>\r\n    <p>London is the capital city of England.</p>\r\n    <p>It is the most populous city in the United Kingdom, with a metropolitan area of over 13 million inhabitants.</p>\r\n  </div>\r\n\r\n  <div id=\"Paris\" class=\"w3-container city\" style=\"display:none\">\r\n    <h2>Paris</h2>\r\n    <p>Paris is the capital of France.</p> \r\n    <p>The Paris area is one of the largest population centers in Europe, with more than 12 million inhabitants.</p>\r\n  </div>\r\n\r\n  <div id=\"Tokyo\" class=\"w3-container city\" style=\"display:none\">\r\n    <h2>Tokyo</h2>\r\n    <p>Tokyo is the capital of Japan.</p>\r\n    <p>It is the center of the Greater Tokyo Area, and the most populous metropolitan area in the world.</p>\r\n  </div>\r\n\r\n</div>\r\n\r\n<script>\r\nfunction openCity(evt, cityName) {\r\n  var i, x, tablinks;\r\n  x = document.getElementsByClassName(\"city\");\r\n  for (i = 0; i < x.length; i++) {\r\n    x[i].style.display = \"none\";\r\n  }\r\n  tablinks = document.getElementsByClassName(\"tablink\");\r\n  for (i = 0; i < x.length; i++) {\r\n    tablinks[i].className = tablinks[i].className.replace(\" w3-red\", \"\"); \r\n  }\r\n  document.getElementById(cityName).style.display = \"block\";\r\n  evt.currentTarget.className += \" w3-red\";\r\n}\r\n</script>\r\n\r\n</body>\r\n</html>\r\n', 'Real Estate Virtual Assistants', '', 'inherit', 'closed', 'closed', '', '193-revision-v1', '', '', '2019-09-07 14:23:17', '2019-09-07 14:23:17', '', 193, 'http://localhost/patronecstest/2019/09/07/193-revision-v1/', 0, 'revision', '', 0),
